import { FETCH_MYLIST, FETCH_RECOMMENDATIONS, REMOVE_FROM_MYLIST } from '../actions/index';

export default function (state = {}, action) {
    switch (action.type) {
      case FETCH_MYLIST:
        return { ...state, 
                mylist:action.payload.data.result}
      case FETCH_RECOMMENDATIONS:
        return { ...state, 
          recommendations:action.payload.data.result}
      case REMOVE_FROM_MYLIST:
        return { ...state,
          mylist_update: state.mylist_update.filter(list => list !== action.payload)
        }
    }
  }
