import axios from 'axios';

export const FETCH_MYLIST = 'FETCH_MYLIST';
export const FETCH_RECOMMENDATIONS = 'FETCH_RECOMMENDATIONS';

export function fetchMylist(){
    const request = axios.get('http://localhost:5000/mylist/')
                    .then(res => {
                      return res
                    })
                    .catch(error=>{
                      console.log(error)
                    })
    return {
        type: FETCH_MYLIST,
        payload: request
      }
}

