import React, { Component } from 'react'
// import { bindActionCreators } from "redux";
import { connect } from 'react-redux';

import { fetchMylist, fetchRecommendations } from '../store/actions/index';
import axios from 'axios';

// import AddLogo from '../static/imgs/add.svg';
// import RemoveLogo from '../static/imgs/remove.svg';

const Mylist = props => (
    <div>
      <div className="movieDisplay__container-img">
        <img alt="movies" width="250" height="400" src={`${props.mylist.img}`}/>         
          <h3>{props.mylist.title}</h3>
            <button onClick={ () => {props.removeFromMylist(props.mylist);props.removeMylistDatabase(props.mylist);} } className="movieDisplay__container__button-btnMyList">
            {/* <button onClick={() =>{ props.removeMylistDatabase(props.mylist); props.onRemoveClick(props.mylist);}} className="movieDisplay__container__button-btnMyList"> */}
              {/* <img src={RemoveLogo} className="movieDisplay__container__button-btnMyList-add"/> */}
              {/* <RemoveLogo className="movieDisplay__container__button-btnMyList-add"/> */}
              Remove From My List
            </button>
        </div>
      </div>
)

const Recommendation = props => (
    <div>
    <div className="movieDisplay__container-img">
      <img width="250" height="400" src={`${props.recommendations.img}`}/>
    </div>
    <h3>{props.recommendations.title}</h3>
        <button onClick={ () => {props.addToMylist(props.recommendations);props.addMylistDatabase(props.recommendations);} } className="movieDisplay__container__button-btnMyList">
      {/* <button onClick={() =>{ props.addMylistDatabase(props.recommendations) }} className="movieDisplay__container__button-btnMyList"> */}
        {/* <img src={AddLogo} className="movieDisplay__container__button-btnMyList-add" /> */}
          Add To My List
      </button>
    </div>
)  

class Movies extends Component {
    constructor(props) {
        super(props)

        this.removeFromMylist = this.removeFromMylist.bind(this);
        this.addToMylist = this.addToMylist.bind(this);
        this.removeMylistDatabase = this.removeMylistDatabase.bind(this) 
        this.addMylistDatabase = this.addMylistDatabase.bind(this)

        this.state = {
             mylist:[],
             recommendations:[]
        }
    }

    componentDidMount() {
        axios.get('http://localhost:5000/mylist/')
        .then(res => {
            this.setState({
                mylist:res.data
            })
        })
        .catch(error=>{
            console.log(error)
        })

        axios.get('http://localhost:5000/recommendations/')
        .then(res => {
            this.setState({
                recommendations:res.data
            })
        })
        .catch(error=>{
            console.log(error)
        })
        }


    removeFromMylist(list){
        this.setState({
            mylist: this.state.mylist.filter(el => el._id !== list._id),
            recommendations:this.state.recommendations.concat(list)
        })
    }

    addToMylist(recommendation){
        this.setState({
            recommendations: this.state.recommendations.filter(el => el._id !== recommendation._id),
            mylist:this.state.mylist.concat(recommendation)
        })
    }

    // update the database at the same time
    removeMylistDatabase(list) {
        axios.post('http://localhost:5000/recommendations/add/',list)
          .then(response => { console.log(response.data)});
        axios.delete('http://localhost:5000/mylist/'+list._id)
          .then(response => { console.log(response.data)});
        this.setState({
          recommendations:JSON.parse(this.state.recommendations).push(list),
          mylist: this.state.mylist.filter(el => el._id !== list._id)
        })
      }

    addMylistDatabase(recommendation){
        axios.post('http://localhost:5000/mylist/add/', recommendation)
            .then(response => { console.log(response.data)});
        axios.delete('http://localhost:5000/recommendations/'+recommendation._id)
          .then(response => { console.log(response.data)});
        this.setState({
          mylist:JSON.parse(this.state.mylist).push(recommendation),
          recommendations: this.state.recommendations.filter(el => el._id !== recommendation._id)
        })
    }


    myList() {
        return this.state.mylist.map(currentmylist => {
          return <Mylist mylist={currentmylist} removeFromMylist={this.removeFromMylist} emoveMylistDatabase={this.removeMylistDatabase} key={currentmylist._id}/>;
        //   return <Mylist mylist={currentmylist} removeMylistDatabase={this.removeMylistDatabase} key={currentmylist._id}/>;
        })
      }

    recommendations() {
        return this.state.recommendations.map(currentRecommendations => {
            return <Recommendation recommendations={currentRecommendations} addToMylist={this.addToMylist} addMylistDatabase={this.addMylistDatabase} key={currentRecommendations._id}/>;
            // return <Recommendation recommendations={currentRecommendations} addMylistDatabase={this.addMylistDatabase} key={currentRecommendations._id}/>;
        })
    }

    render() {
      return (
        <div>
            <h2 className="movieDisplay__heading">My List</h2>
            <div className="movieDisplay__container">{ this.myList() }</div>
            <h3 className="movieDisplay__heading">Recommendations</h3>
            <div className="movieDisplay__container">{ this.recommendations() }</div>
        </div>
      )
    }
  }

    // const mapStateToProps = (state) => {
    // return { mylist: state.mylist, recommendations: state.recommendation, mylist_update: state.mylist_update }
    // }
  const mapStateToProps = (state) => {
        return { mylist: state.mylist, recommendations: state.recommendations }
  }

  const mapDispatchToProps = (dispatch) => ({
        fetchMylist:()=>dispatch(fetchMylist()), 
        fetchRecommendations:()=>dispatch(fetchRecommendations())
  })

//     const mapDispatchToProps = (dispatch) => ({
//         fetchMylist:()=>dispatch(fetchMylist()), 
//         fetchRecommendations:()=>dispatch(fetchRecommendations()),
//         onRemoveClick:()=>dispatch(removeFromMylist())
//   })

  export default connect(mapStateToProps, mapDispatchToProps)(Movies);
