import React, { Component } from 'react'
import { bindActionCreators } from "redux";
import { connect } from 'react-redux';

import { fetchMylist } from '../store/actions/index';

const Mylist = props => (
    <div>
      <div className="movieDisplay__container-img">
        <img alt="movies" width="250" height="400" src={`${props.mylist.img}`}/>         
          <h3>{props.mylist.title}</h3>
            {/* <button onClick={() =>{ props.removeMylist(props.mylist) }} className="movieDisplay__container__button-btnMyList">
              <img src={RemoveLogo} className="movieDisplay__container__button-btnMyList-add"/>
              Remove From My List
            </button> */}
        </div>
      </div>
)

class Movies extends Component {
    constructor(props) {
        super(props)

        // this.removeMylist = this.removeMylist.bind(this) 
        // this.addMylist = this.addMylist.bind(this)
        this.state = {
             mylist:[],
             recommendations:[]
        }
    }

    componentWillMount() {
        this.mylist = this.setState(this.props.fetchMylist());
    }

    myList() {
        return this.state.mylist.map(currentmylist => {
          return <Mylist mylist={currentmylist} key={currentmylist._id}/>;
        })
      }

    render() {
      return (
        <>
          <h1 className="movieDisplay__heading">My list</h1>
          <div className="movieDisplay__container">
            {this.myList()}
          </div>
        </>
      )
    }
  }

  const mapStateToProps = (state) => {
    return { mylist: state}
  }

  const mapDispatchToProps = (dispatch) => {
    return bindActionCreators({ fetchMylist }, dispatch)
  }

  export default connect(mapStateToProps, mapDispatchToProps)(Movies);
