import React, { Component } from 'react'
import axios from 'axios'

const Mylist = props => (
    <tr>
      <td>{props.mylist.title}</td>
      <td><img width="220" height="220" src={`${props.mylist.img}`}/></td>
      <td>
        <a href="#" onClick={() =>{ props.removeMylist(props.mylist._id) }}>Remove</a>
      </td>
    </tr>
  )

const Recommendation = props => (
  <tr>
    <td><img width="220" height="220" src={`${props.recommendations.img}`}/></td>
    <h3>{props.recommendations.title}</h3>
    <td>
        <a href="#" onClick={() =>{ props.addMylist(props.recommendations) }}>Add</a>
    </td>
  </tr>
)  

class MovieList extends Component {
    constructor(props) {
        super(props)

        this.removeMylist = this.removeMylist.bind(this) 
        this.addMylist = this.addMylist.bind(this)
        this.state = {
             mylist:[],
             recommendations:[]
        }
    }

    componentDidMount(){
        axios.get('http://localhost:5000/mylist/')
        .then(res => {
            this.setState({
                mylist:res.data
            })
        })
        .catch(error=>{
            console.log(error)
        })

        axios.get('http://localhost:5000/recommendations/')
        .then(res => {
            this.setState({
                recommendations:res.data
            })
        })
        .catch(error=>{
            console.log(error)
        })

        // axios.post('http://localhost:5000/recommendations/', this.removeMylist())
        // .then(() =>{console.log("updated in recommendations!")})
    }

    removeMylist(id) {
        // axios.post('http://localhost:5000/recommendations/'+id)
        //   .then(response => { console.log(response.data)});
        axios.delete('http://localhost:5000/mylist/'+id)
          .then(response => { console.log(response.data)});
        this.setState({
          mylist: this.state.mylist.filter(el => el._id !== id)
        })
      }

    addMylist(recommendation){
        axios.post('http://localhost:5000/mylist/add/', recommendation)
            .then(response => { console.log(response.data)});
        // this.setState({
        //   // mylist: this.state.mylist.filter(el => el.push(recommendations))
        //   mylist:JSON.parse(this.state.mylist).push(recommendation)
        // });
        axios.delete('http://localhost:5000/recommendations/'+recommendation._id)
          .then(response => { console.log(response.data)});
        this.setState({
          mylist:JSON.parse(this.state.mylist).push(recommendation),
          recommendations: this.state.recommendations.filter(el => el._id !== id)
        })
        // axios.delete('http://localhost:5000/recommendations/'+id)
        //   .then(response => { console.log(response.data)});
        // this.setState({
        //   recommendations: this.state.recommendations.filter(el => el._id !== id)
        // })
    }

    myList() {
      return this.state.mylist.map(currentmylist => {
        return <Mylist mylist={currentmylist} removeMylist={this.removeMylist} key={currentmylist._id}/>;
      })
    }

    recommendations() {
      return this.state.recommendations.map(currentRecommendations => {
        return <Recommendation recommendations={currentRecommendations} addMylist={this.addMylist} key={currentRecommendations._id}/>;
      })
    }

    render() {
        return (
            <div>
                <table className="table">
                  <thead className="thead-light">
                    <h2>My list</h2>
                    <th>{ this.myList() }</th>
                    <h3>Recommendations</h3>
                    <th>{ this.recommendations() }</th>
                  </thead>
                </table>
            </div>
        )
    }
}

export default MovieList
