import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import axios from 'axios'

//Functional react component
const Movie = props => (
    <tr>
      <td>{props.movie.mylist.title}</td>
      <td>{props.movie.mylist.img}</td>
      <td>
        <Link>Add</Link> | <a href="#" onClick={() =>
         { props.removeMovie(props.movie.mylist._id) }}>Remove</a>
      </td>
    </tr>
  )

class MovieList extends Component {
    constructor(props) {
        super(props)

        this.removeMovie = this.removeMovie.bind(this) 
        this.state = {
             mylist:[]
        }
    }

    componentDidMount(){
        axios.get('http://localhost:5000/movies/')
        .then(res => {
            this.setState({
                mylist:res.data
            })
        })
        .catch(error=>{
            console.log(error)
        })
    }

    removeMovie(id) {
        axios.delete('http://localhost:5000/movies/'+id)
          .then(response => { console.log(response.data)});

        this.setState({
          mylist: this.state.movie.mylist.filter(el => el._id !== id)
        })
      }

      myList() {
        return this.state.movie.mylist.map(currentmylist => {
          return <Movie mylist={currentmylist} removeMovie={this.removeMovie} key={currentmylist._id}/>;
        })
      }

    render() {
        return (
            <div>
                <table className="table">
                <thead className="thead-light">
                    <tr>
                    <th>Title</th>
                    <th>Img</th>
                    </tr>
                </thead>
                <tbody>
                    { this.mylist() }
                </tbody>
                </table>
            </div>
        )
    }
}

export default MovieList
