import React, { Component } from 'react'
import { bindActionCreators } from "redux";
import { connect } from 'react-redux';

import { fetchMylist, fetchRecommendations } from '../store/actions/index';
import axios from 'axios';

import AddLogo from '../static/imgs/add.svg';
import RemoveLogo from '../static/imgs/remove.svg';

const Mylist = props => (
    <div>
      <div className="movieDisplay__container-img">
        <img alt="movies" width="250" height="400" src={`${props.mylist.img}`}/>         
          <h3>{props.mylist.title}</h3>
            <button onClick={() =>{ props.removeMylist(props.mylist) }} className="movieDisplay__container__button-btnMyList">
              <img src={RemoveLogo} className="movieDisplay__container__button-btnMyList-add"/>
              {/* <RemoveLogo className="movieDisplay__container__button-btnMyList-add"/> */}
              Remove From My List
            </button>
        </div>
      </div>
)

const Recommendation = props => (
    <div>
    <div className="movieDisplay__container-img">
      <img width="250" height="400" src={`${props.recommendations.img}`}/>
    </div>
    <h3>{props.recommendations.title}</h3>
      <button onClick={() =>{ props.addMylist(props.recommendations) }} className="movieDisplay__container__button-btnMyList">
        <img src={AddLogo} className="movieDisplay__container__button-btnMyList-add" />
        {/* <AddLogo className="movieDisplay__container__button-btnMyList-add"/> */}
          Add To My List
      </button>
    </div>
)  

class Movies extends Component {
    constructor(props) {
        super(props)

        // this.removeMylist = this.removeMylist.bind(this) 
        // this.addMylist = this.addMylist.bind(this)
        this.state = {
             mylist:[],
             recommendations:[]
        }
    }

    componentDidMount() {
        axios.get('http://localhost:5000/mylist/')
        .then(res => {
            this.setState({
                mylist:res.data
            })
        })
        .catch(error=>{
            console.log(error)
        })

        axios.get('http://localhost:5000/recommendations/')
        .then(res => {
            this.setState({
                recommendations:res.data
            })
        })
        .catch(error=>{
            console.log(error)
        })
    }

    // update the database at the same time
    removeMylistDatabase(list) {
        axios.post('http://localhost:5000/recommendations/add/',list)
          .then(response => { console.log(response.data)});
        axios.delete('http://localhost:5000/mylist/'+list._id)
          .then(response => { console.log(response.data)});
        this.setState({
          recommendations:JSON.parse(this.state.recommendations).push(list),
          mylist: this.state.mylist.filter(el => el._id !== list._id)
        })
      }

    addMylistDatabase(recommendation){
        axios.post('http://localhost:5000/mylist/add/', recommendation)
            .then(response => { console.log(response.data)});
        axios.delete('http://localhost:5000/recommendations/'+recommendation._id)
          .then(response => { console.log(response.data)});
        this.setState({
          mylist:JSON.parse(this.state.mylist).push(recommendation),
          recommendations: this.state.recommendations.filter(el => el._id !== recommendation._id)
        })
    }


    myList() {
        return this.state.mylist.map(currentmylist => {
          return <Mylist mylist={currentmylist} removeMylistDatabase={this.removeMylistDatabase} key={currentmylist._id}/>;
        })
      }

    recommendations() {
        return this.state.recommendations.map(currentRecommendations => {
            return <Recommendation recommendations={currentRecommendations} addMylistDatabase={this.addMylistDatabase} key={currentRecommendations._id}/>;
        })
    }

    render() {
      return (
        <div>
            <h2 className="movieDisplay__heading">My List</h2>
            <div className="movieDisplay__container">{ this.myList() }</div>
            <h3 className="movieDisplay__heading">Recommendations</h3>
            <div className="movieDisplay__container">{ this.recommendations() }</div>
        </div>
      )
    }
  }

  const mapStateToProps = (state) => {
    return { mylist: state, recommendations: state }
  }

  const mapDispatchToProps = (dispatch) => {
    return bindActionCreators({ fetchMylist, fetchRecommendations }, dispatch)
  }

  export default connect(mapStateToProps, mapDispatchToProps)(Movies);
