import React, { Component } from 'react'
import axios from 'axios'

const Mylist = props => (
    <tr>
      <td>{props.mylist.title}</td>
      <td><img width="220" height="220" src={`${props.mylist.img}`}/></td>
      <td>
        <a href="#" onClick={() =>{ props.removeMylist(props.mylist._id) }}>Remove</a>
      </td>
    </tr>
  )

class MyList extends Component {
    constructor(props) {
        super(props)

        this.removeMylist = this.removeMylist.bind(this) 
        this.state = {
             mylist:[]
        }
    }

    componentDidMount(){
        axios.get('http://localhost:5000/mylist/')
        .then(res => {
            this.setState({
                mylist:res.data
            })
        })
        .catch(error=>{
            console.log(error)
        })
    }

    removeMylist(id) {
        axios.delete('http://localhost:5000/mylist/'+id)
          .then(response => { console.log(response.data)});
        axios.get('http://localhost:5000/recommendations/'+id)
          .then(response => { console.log(response.data)});
        this.setState({
          mylist: this.state.mylist.filter(el => el._id !== id)
        })
      }

      myList() {
        return this.state.mylist.map(currentmylist => {
          return <Mylist mylist={currentmylist} removeMylist={this.removeMylist} key={currentmylist._id}/>;
        })
      }

    render() {
        return (
            <div>
                <table className="table">
                <thead className="thead-light">
                  <th>{ this.myList() }</th>
                </thead>
                </table>
            </div>
        )
    }
}

export default MyList
