import React, { useEffect } from 'react'
import { connect } from 'react-redux';
import { fetchMovies } from '../store/actions/export';

function Display({ data, fetchMovies }){
    useEffect(()=>{
        fetchMovies();
    }, []);
    return data.loading ? (<h2>Loading</h2>) : data.error ? (<h2>{data.error}</h2>):(
        <div>
            <h1>My List</h1>
            <p>{"Data is: ",console.log(data)}</p>
            <div>
                {data && data.mylist && data.mylist.map(function(mylist){
                    return (
                        <div>
                            <img src={mylist.img} alt="p1"/>
                        </div>
                    )
                })}
            </div>
        </div>
    )
}

const mapStateToProps = (state) => {
    return {
      data: state.data,
    }
};

const mapDispatchToProps = (dispatch) => {
    return {
      fetchMovies: () => dispatch(fetchMovies())
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(Display);
