import React, { Component } from 'react'
import axios from 'axios'
import { Container, Row, Col } from 'reactstrap';
import AddLogo from '../static/imgs/add.svg';
import RemoveLogo from '../static/imgs/remove.svg';

const Mylist = props => (

    <div class="movie-poster-only col-6 col-md-4">
      <div><img src={`${props.mylist.img}`}/></div>      
      <div class="operation__container">
        <p>{props.mylist.title}</p>
        <button onClick={() =>{ props.removeMylist(props.mylist) }} className="operation__container-btnMyList">
          <img src={RemoveLogo} />
        </button>
      </div>
      </div>
  )

const Recommendation = props => (
    <div className="movie-poster-only col-6 col-md-4">
    <td><img src={`${props.recommendations.img}`}/></td>
    <h3>{props.recommendations.title}</h3>
      <button onClick={() =>{ props.addMylist(props.recommendations) }} className="operation__container-btnMyList">
        <img src={AddLogo} className="operation__container-btnMyList-add" />
          My List
      </button>
    </div>
)  

class MovieList extends Component {
    constructor(props) {
        super(props)

        this.removeMylist = this.removeMylist.bind(this) 
        this.addMylist = this.addMylist.bind(this)
        this.state = {
             mylist:[],
             recommendations:[]
        }
    }

    componentDidMount(){
        axios.get('http://localhost:5000/mylist/')
        .then(res => {
            this.setState({
                mylist:res.data
            })
        })
        .catch(error=>{
            console.log(error)
        })

        axios.get('http://localhost:5000/recommendations/')
        .then(res => {
            this.setState({
                recommendations:res.data
            })
        })
        .catch(error=>{
            console.log(error)
        })

    }

    removeMylist(list) {
        axios.post('http://localhost:5000/recommendations/add/',list)
          .then(response => { console.log(response.data)});
        axios.delete('http://localhost:5000/mylist/'+list._id)
          .then(response => { console.log(response.data)});
        this.setState({
          recommendations:JSON.parse(this.state.recommendations).push(list),
          mylist: this.state.mylist.filter(el => el._id !== list._id)
        })
      }

    addMylist(recommendation){
        axios.post('http://localhost:5000/mylist/add/', recommendation)
            .then(response => { console.log(response.data)});
        axios.delete('http://localhost:5000/recommendations/'+recommendation._id)
          .then(response => { console.log(response.data)});
        this.setState({
          mylist:JSON.parse(this.state.mylist).push(recommendation),
          recommendations: this.state.recommendations.filter(el => el._id !== recommendation._id)
        })
    }

    myList() {
      return this.state.mylist.map(currentmylist => {
        return <Mylist mylist={currentmylist} removeMylist={this.removeMylist} key={currentmylist._id}/>;
      })
    }

    recommendations() {
      return this.state.recommendations.map(currentRecommendations => {
        return <Recommendation recommendations={currentRecommendations} addMylist={this.addMylist} key={currentRecommendations._id}/>;
      })
    }

    render() {
        return (
            <div>
                {/* <div className="movies-posters-list"> */}
                    <h2 className="movieDisplay__heading">My list</h2>
                    <div className="movieShowcase__container">{ this.myList() }</div>
                    <h3>Recommendations</h3>
                    <div>{ this.recommendations() }</div>
                  {/* </div> */}
            </div>
        )
    }
}

export default MovieList
