import React, { Component } from 'react'
import { bindActionCreators } from "redux";
import { connect } from 'react-redux';

import { fetchMylist } from '../store/actions/index';

const Mylist = props => (
    <div>
      <div className="movieDisplay__container-img">
        <img width="250" height="400" src={`${props.mylist.img}`}/>         
          <h3>{props.mylist.title}</h3>
            <button onClick={() =>{ props.removeMylist(props.mylist) }} className="movieDisplay__container__button-btnMyList">
              {/* <img src={RemoveLogo} className="movieDisplay__container__button-btnMyList-add"/> */}
              Remove From My List
            </button>
        </div>
      </div>
)

class Movies extends Component {

    componentWillMount() {
      this.props.fetchMylist();
    }

    // myList(mylist) {
    //     return this.state.mylist.map(currentmylist => {
    //       return <Mylist mylist={currentmylist} key={currentmylist._id}/>;
    //     })
    //   }

    getMovieDisplay(movies) {
        return movies.map((currentmylist) => {
            return <Mylist mylist={currentmylist} key={currentmylist._id}/>;
        });
    }  

    render() {
    //   let movies
    //   if (this.props.trending.data) {
    //     const url = '/trending/all/week?api_key=224ce27b38a3805ecf6f6c36eb3ba9d0&language=en-US';
    //     movies = getMovieRows(this.props.trending.data, url);
    //   }
      return (
        <>
          <h1 className="movieDisplay__heading">Trending Now</h1>
          <div className="movieDisplay__container">
            { getMovieDisplay(this.props.mylist.data) }
          </div>
        </>
      )
    }
  }

  const mapStateToProps = (state) => {
    return { mylist: state }
  }

  const mapDispatchToProps = (dispatch) => {
    return bindActionCreators({ fetchMylist }, dispatch)
  }

  export default connect(mapStateToProps, mapDispatchToProps)(Movies);
