import { REMOVE_FROM_MYLIST } from '../actions/index';

export default function (state, action){
    switch (action.type) {
        case REMOVE_FROM_MYLIST:
        return { ...state,
                mylist_update: state.mylist_update.filter(list => list !== action.payload)}
        default:
            return state;
    }
}
