import { FETCH_DATA_REQUEST, FETCH_DATA_SUCCESS, FETCH_DATA_FAILURE,
         REMOVE_MOVIE, ADD_MOVIE} from '../reducers/fetchTypes'

const initialState = {
  loading:false,
  mylist:[],
  recommendations:[],
  error:""
};

const reducer = (state = initialState, action) => {
    switch (action.type) {
      case FETCH_DATA_REQUEST:
        return {...state, loading: true}
      case FETCH_DATA_SUCCESS:
        return { loading:false, 
                mylist:action.payload.mylist,
                recommendations: action.payload.recommendations,
                error:""}
      case FETCH_DATA_FAILURE:
        return { loading: false,
                  mylist: [],
                  recommendations: [],
                  error:action.payload}
      case REMOVE_MOVIE:
        return {...state,
          mylist: state.mylist.filter(el => el.id !== action.id),
          recommendations:state.recommendations.concat(state.mylist.find(el => el.id === action.id))
        }
      case ADD_MOVIE:
        return {...state,
          mylist: state.mylist.concat(state.recommendations.find(el => el.id === action.id)),
          recommendations:state.recommendations.filter(el => el.id !== action.id)
        }
      default:
        return state;
    }
  }

export default reducer;
