import axios from 'axios';
export const FETCH_MYLIST = 'FETCH_MYLIST';
export const FETCH_RECOMMENDATIONS = 'FETCH_RECOMMENDATIONS';

export function fetchMylist(){
    const mylist = axios.get('../../movies.json', { headers:{ 
                            'Content-Type': 'application/json',
                            'Accept': 'application/json'
                            }})
                    // .then(res => console.log(res.data.json()))
                    // .then(JSON.parse)
                    // .then(res => console.log(res.data))
                    .then(res => res.data.slice(10,320))
                    // .then(res => JSON.parse(JSON.stringify(res.data.mylist)))
                    // .then(res => console.log(res.data.map(val=>{return val})))
                    .catch(error => console.log(error))
    return {
      type: FETCH_MYLIST,
      payload: mylist
    }
}

export function fetchRecommendations(){
  const recommendations = axios.get('../../movies.json')
                        .then(res => res.data.slice(446,-2))
                        .catch(error => console.log(error))
  return {
    type: FETCH_RECOMMENDATIONS,
    payload: recommendations
  }
}
