// export const FETCH_RECOMMENDATIONS = 'FETCH_RECOMMENDATIONS';

export function fetchMylist(){
    const request = axios.get('http://localhost:5000/mylist/')
                    .then(res => {
                      console.log(res.json())
                    })
                    .catch(error=>{
                      console.log(error)
                    })
    return {
        type: FETCH_MYLIST,
        payload: request
