import React, { useEffect } from 'react'
import { connect } from 'react-redux';
import { fetchMovies } from '../store/actions/export';
import { removeMovieAction } from '../store/actions/export';
import { addMovieAction } from '../store/actions/export';

function Display({ data, fetchMovies, removeMovieAction, addMovieAction }){
    useEffect(()=>{
        fetchMovies();
    }, []);
    return data.loading ? (<h2>Loading</h2>) : (
        <div>
            <>
            <div className="movieDisplay__heading">My List</div>
                <div className="movieDisplay__container">
                    {data.mylist.map(function(mylist){
                        return (
                            <div className="movieDisplay__container__dropdown">
                                <div>
                                    <img alt="movies" width="250" height="400" src={mylist.img}/>
                                </div>
                                <div className="movieDisplay__container__dropdown__img-imgoverlay" />
                                <div className="movieDisplay__container__dropdown-content">
                                    <div className="movieDisplay__container__dropdown-content-a">
                                        <button onClick={ () => {removeMovieAction(mylist.id)}} className="movieDisplay__container__dropdown-content__button">REMOVE</button>&nbsp;&nbsp;&nbsp;
                                        {mylist.title}
                                    </div>                              
                                </div> 
                            </div>
                        )
                    })}
                </div>
            </>

            <>       
            <div className="movieDisplay__heading">Recommendations</div>
            <div className="movieDisplay__container">
                {data.recommendations.map(function(recommendations){
                    return (
                        <div className="movieDisplay__container__dropdown">
                            <div>
                                <img alt="movies" width="250" height="400" src={recommendations.img}/>
                            </div>
                            <div className="movieDisplay__container__dropdown__img-imgoverlay" />
                            <div className="movieDisplay__container__dropdown-content">
                                <div className="movieDisplay__container__dropdown-content-a">
                                    <button onClick={ () => {addMovieAction(recommendations.id)}} className="movieDisplay__container__dropdown-content__button">ADD</button>&nbsp;&nbsp;&nbsp;
                                    {recommendations.title}
                                </div>                              
                            </div> 
                        </div>
                    )
                })}
            </div>
            </> 

        </div>
    )
}

const mapStateToProps = (state) => {
    return {
      data: state.data,
    }
};

const mapDispatchToProps = (dispatch) => {
    return {
      fetchMovies: () => dispatch(fetchMovies()),
      removeMovieAction: (movie) => {
          dispatch(removeMovieAction(movie))
      },
      addMovieAction: (movie) => {
          dispatch(addMovieAction(movie))
      }
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(Display);
