import axios from 'axios';
export const FETCH_MYLIST = 'FETCH_MYLIST';
export const FETCH_RECOMMENDATIONS = 'FETCH_RECOMMENDATIONS';

export function fetchMylist(){
    const mylist = fetch('movies.json',
                    {headers:{'Content-Type':'application/json',
                              'Accept':'application/json'}})
                    .then(res => res.data[mylist])
                    .catch(error => console.log(error))
    return {
      type: FETCH_MYLIST,
      payload: mylist
    }
}

export function fetchRecommendations(){
  const recommendations = fetch('movies.json')
                        .then(res => res.data[recommendations])
                        .catch(error => console.log(error))
  return {
    type: FETCH_RECOMMENDATIONS,
    payload: recommendations
  }
}
