import axios from 'axios';
import { FETCH_DATA_REQUEST, FETCH_DATA_SUCCESS, FETCH_DATA_FAILURE} from '../reducers/fetchTypes';

// export const FETCH_DATA_REQUEST = 'FETCH_DATA_REQUEST';
// export const FETCH_DATA_SUCCESS = 'FETCH_DATA_SUCCESS';
// export const FETCH_DATA_FAILURE = 'FETCH_DATA_FAILURE';

export const fetchDataRequest = () =>{
  return { type: FETCH_DATA_REQUEST}
}

export const fetchDataSuccess = (data) =>{
  return { type: FETCH_DATA_SUCCESS,
           payload: data}
}

export const fetchDataFailure = (error) =>{
  return { type: FETCH_DATA_FAILURE,
           payload: error}
}


export const fetchMovies = () => {
  return (dispatch) =>{
        dispatch(fetchDataRequest());
        axios.get('/movies.json')
        .then(res => {
          console.log(res.data)
          const datalist = res.data;
          dispatch(fetchDataSuccess(datalist))
        })
        // .then(res => console.log(res.data.slice(10,332)))
        .catch(error => {
          dispatch(fetchDataFailure(error.message))})
  }
}

