    //     })
    //   }

    recommendations() {
        return this.state.recommendations.map(currentRecommendations => {
          return <Recommendation recommendations={currentRecommendations} key={currentRecommendations._id}/>;
        })
      }

    render() {
        return (
            <div>
                <table className="table">
                <thead className="thead-light">
                  <th>{ this.recommendations() }</th>
                </thead>
                </table>
            </div>
        )
    }
}

export default Recommendations
