import React, { Component } from 'react'
// import DatePicker from 'react-datepicker'
// import "react-datepicker/dist/react-datepicker.css"
import axios from 'axios'

class CreateMyList extends Component {
    constructor(props) {
        super(props)

        // this.onSubmit = this.onSubmit.bind(this);

        this.state = {
             title:'',
             img:'',
             mylist:[]
        }
    }

    //lifecycle hook, just before render
    componentDidMount(){
        axios.get('http://localhost:5000/movie/')
        .then( res => {
            if(res.data.length > 0){
                this.setState({
                    mylist: res.data.map(mylist => mylist.mylist.title)
                })
            }
        })

    }

    //   onSubmit(e){
    //       e.preventDefault();

    //       const exercise = {
    //         username: this.state.username,
    //         description: this.state.description,
    //         duration: this.state.duration,
    //         date: this.state.date
    //       }
    //       console.log(exercise)

    //     axios.post('http://localhost:5000/exercises/add', exercise)
    //     .then(res => console.log(res.data))

    //     window.location = '/'
    //   }


    render() {
        return (
            <div>
                <h3>Create My List</h3>
                    {/* <form onSubmit={this.onSubmit}> */}
                        <div className="form-group"> 
                        <label>Title: </label>
                        <select ref="userInput"
                            required
                            className="form-control"
                            value={this.state.title}>
                            {
                                this.state.mylist.map(function(mylist) {
                                return <option 
                                    key={mylist}
                                    value={mylist}>{mylist}
                                    </option>;
                                })
                            }
                        </select>
                        </div>
                    {/* </form> */}
            </div>
        )
    }
}

export default CreateMyList
