import { FETCH_DATA } from '../actions/index';

export default function (state = {}, action) {
    switch (action.type) {
      case FETCH_MYLIST:
        return { ...state, 
                mylist:action.payload.mylist}
      // default:
      //   return state;
      case FETCH_RECOMMENDATIONS:
        return { ...state, 
                mylist:action.payload.recommendations}
      // default:
      //   return state;
    }
  }
