import axios from 'axios';
export const FETCH_MYLIST = 'FETCH_MYLIST';
export const FETCH_RECOMMENDATIONS = 'FETCH_RECOMMENDATIONS';

export function fetchMylist(){
    const mylist = axios.get('movies.json')
                    .then(res => res.data.slice(15,416))
                    .catch(error => console.log(error))
    return {
      type: FETCH_MYLIST,
      payload: mylist
    }
}

export function fetchRecommendations(){
  const recommendations = axios.get('movies.json')
                        .then(res => res.data.slice(446,-2))
                        .catch(error => console.log(error))
  return {
    type: FETCH_RECOMMENDATIONS,
    payload: recommendations
  }
}
