export const FETCH_MYLIST = 'FETCH_MYLIST';
export const FETCH_RECOMMENDATIONS = 'FETCH_RECOMMENDATIONS';
export const REMOVE_FROM_MYLIST = 'REMOVE_FROM_MYLIST';

export function fetchMylist(mylist){
    return {
      type: FETCH_MYLIST,
      payload: mylist
    }
}

export function fetchRecommendations(recommendations){
  return {
    type: FETCH_RECOMMENDATIONS,
    payload: recommendations
  }
}

export function removeFromMylist(mylist_update){
  return {
    type: REMOVE_FROM_MYLIST,
    payload: mylist_update
  }
}
